import { FunctionComponent, useCallback, useEffect, useState } from "react";
import style from "./css/Mensagem.module.css"
import PortalPopup from "../../Components/PortalPopup";
import { ETipoMensagem } from "../../Enuns/ETipoMensagem";

type ModalUsuarioType = {
  TipoMensagem: ETipoMensagem;
  Mensagem: string|undefined|unknown;
  onClose?: () => void;  
};

const ImegemDone = "./IconsMensagens/Done.svg"
const ImegemErro = "./IconsMensagens/Erro.svg"
const ImegemAviso = "./IconsMensagens/Aviso.svg"
const ImegemConfirmacao = "./IconsMensagens/Confirmacao.svg"
let Imagem: string | undefined;
let Botoes: {} | null | undefined;  
const Mensagem: FunctionComponent<ModalUsuarioType> = ({onClose, Mensagem, TipoMensagem}) => {
    const [isModalUsuarioPopupOpen, setModalUsuarioPopupOpen] = useState(true);
    const closeModalUsuarioPopup = useCallback(() => {
        setModalUsuarioPopupOpen(false);
      }, []);
      
          
      if(Mensagem === undefined)
      {
        Mensagem = "Atenção! Não foi passado nenhum Texto para essa mensagem!"
      }
      if(TipoMensagem === ETipoMensagem.Done)
      {
        Imagem = ImegemDone;  
        Botoes = (<button className={style.BotaoDone} onClick={closeModalUsuarioPopup}>OK</button>)      
      }
      else if(TipoMensagem === ETipoMensagem.Erro)
      {
        Imagem = ImegemErro;
        Botoes = (<button className={style.BotaoErro} onClick={closeModalUsuarioPopup}>OK</button>)
      }
      else if(TipoMensagem === ETipoMensagem.Aviso)
      {
        Imagem = ImegemAviso;
        Botoes = (<button className={style.BotaoAviso} onClick={closeModalUsuarioPopup}>OK</button>)
      }
      else if(TipoMensagem === ETipoMensagem.Confirmacao)
      {
        Imagem = ImegemConfirmacao;
        Botoes = (<div className={style.BotoesConfirmacao}><button className={style.BotaoCancelar} onClick={onClose}>Cancelar</button> <button className={style.BotaoConfirmar} onClick={closeModalUsuarioPopup}>Confirmar</button></div>)
      }

      useEffect(()=>{        
        let Icon = document.getElementById("Icon"); 
        
        if(Icon !=null)
        {
          if(TipoMensagem === ETipoMensagem.Done)
          {
            Icon.style.backgroundColor = "#82ce34";       
          }
          else if(TipoMensagem === ETipoMensagem.Erro)
          {
            Icon.style.backgroundColor = "#ef513a";
          }
          else if(TipoMensagem === ETipoMensagem.Aviso)
          {
            Icon.style.backgroundColor = "#f2b600";
          }
          else if(TipoMensagem === ETipoMensagem.Confirmacao)
          {
            Icon.style.backgroundColor = "#0055ff";            
          }
        }else{
          alert("GetById Error")
        }
      }, []);  
    
    return (  
      <>    
        {isModalUsuarioPopupOpen && (
          <PortalPopup
                zIndex={99}
                overlayColor="rgba(113, 113, 113, 0.3)"
                placement="Top center"
                onOutsideClick={closeModalUsuarioPopup}
              >
                <div className={style.Mensagem}>
                  <div className={style.Body}>
                    <div id="Icon" className={style.Icon + " " + style.ColorPadrao}>
                      <img  className={style.Imagem} src={Imagem} />
                    </div>
                    <div className={style.TextoMensagem}>
                      {Mensagem}
                    </div>
                    <div>
                      {Botoes}                   
                    </div>
                  </div>
                </div>            
          </PortalPopup>
        )}
      </>
    ); 
  
};
export default Mensagem;
